func ANSDecodeDeltas(numDeltas int, inp []byte, inpSize int, R float64) ([]byte, error) {
	dt = C.FSE_createDTable(C.uint(tableLog))

	nCount, ok := kRCount[R]
	if !ok {
		return nil, fmt.Errorf("R: %f not existed ", R)
	}
	nCountData := (*C.short)(unsafe.Pointer(&nCount[0]))
	nCountLen := len(nCount)

	C.FSE_buildDTable(dt, nCountData, C.uint(nCountLen), C.uint(tableLog))

	out := make([]byte, 10000)

	C.FSE_decompress_usingDTable(
		unsafe.Pointer(&out[0]),
		C.ulong(numDeltas),
		unsafe.Pointer(&inp[0]),
		C.ulong(inpSize),
		dt,
	)
	return out, nil
}
